function do_something($some_argument) {\n    try {\n        throw new Exception();\n    } catch (Exception $e) {\n        $trace = $e->getTrace()[0];\n        $variableName = getVariableName($trace);\n        echo $variableName; // Outputs: contact_name\n    }\n}\n\nfunction getVariableName($trace) {\n    $line = file($trace['file']);\n    $line = $line[$trace['line'] - 1];\n    $matches = array();\n    preg_match('/\((.*?)\)/', $line, $matches);\n    $matches = explode(',', $matches[1]);\n    return trim($matches[0]);\n}\n\n$contact_name = 'foo';\ndo_something($contact_name);