class Person {\n    private $name;\n    private $email;\n\n    public function getName() {\n        return $this->name;\n    }\n\n    public function getEmail() {\n        return $this->email;\n    }\n\n    public function setName($name) {\n        $this->name = filter_var($name, FILTER_SANITIZE_STRING);\n    }\n\n    public function setEmail($email) {\n        if (!filter_var($email, FILTER_VALIDATE_EMAIL)) {\n            return false;\n        }\n        $this->email = $email;\n        return true;\n    }\n}