$text = 'your document text here...';\n\n// Use preg_match_all to grab all matches\npreg_match_all('/<p>.+?<\/p>/s', $text, $matches);\n\n// Shuffle the matches array\nshuffle($matches[0]);\n\n// Select a random match\n$randomMatch = $matches[0][0];\n\n// Save it to a variable\n$selectedText = $randomMatch;\n\n// Remove the <p> tags if needed\n$selectedText = strip_tags($selectedText);\n\necho $selectedText;