/**\n * Implementation of hook_form_alter().\n */\nfunction mymodule_form_alter(&$form, &$form_state, $form_id) {\n  if ($form_id == 'system_site_information_settings') {\n    // Add a new text field\n    $form['my_custom_field'] = array(\n      '#type' => 'textfield',\n      '#title' => t('My Custom Field'),\n      '#description' => t('Enter a value for my custom field'),\n      '#default_value' => variable_get('my_custom_field', ''),\n    );\n  }\n}\n\n/**\n * Implementation of hook_validate().\n */\nfunction mymodule_validate($form, &$form_state) {\n  if ($form_state['values']['my_custom_field'] == '') {\n    form_set_error('my_custom_field', t('My Custom Field is required'));\n  }\n}\n\n/**\n * Implementation of hook_submit().\n */\nfunction mymodule_submit($form, &$form_state) {\n  variable_set('my_custom_field', $form_state['values']['my_custom_field']);\n}