$thisTime = gmmktime(0, 0, 0);\n\nfor ($i = 0; $i <= 95; $i++) {\n    $perfTimeNumber = ($i + 1);\n    $perfTimestamp = $thisTime;\n    $perfTime = date("H:i", $perfTimestamp);\n    $sel = ''; // Reset the $sel variable\n    if ($perfTime == '19:30') {\n        $sel = "selected";\n    }\n    echo '<option value="' . $perfTimeNumber . '" ' . $sel . '>' . $perfTime . '</option>';\n    $thisTime = $thisTime + (15 * 60);\n}