$xmlFile = 'path/to/your/large/xml/file.xml';\n$targetTags = array('specific_tag_1', 'specific_tag_2'); // tags you're interested in\n\n$xmlReader = new XMLReader();\n$xmlReader->open($xmlFile);\n\nwhile ($xmlReader->read()) {\n    if ($xmlReader->nodeType === XMLReader::ELEMENT && in_array($xmlReader->name, $targetTags)) {\n        $tagContent = $xmlReader->readString();\n        // write the extracted content to a flat TXT file\n        file_put_contents('output.txt', $tagContent . PHP_EOL, FILE_APPEND);\n    }\n}\n\n$xmlReader->close();