function findSecondToLastOccurrence($str, $char) {\n    $lastOccurrence = strrpos($str, $char);\n    if ($lastOccurrence === false) {\n        return false; // character not found\n    }\n    $strBeforeLastOccurrence = substr($str, 0, $lastOccurrence);\n    $secondToLastOccurrence = strrpos($strBeforeLastOccurrence, $char);\n    if ($secondToLastOccurrence === false) {\n        return false; // character appears less than two times\n    }\n    return $secondToLastOccurrence;\n}\n\n// test the function\n$string = 'Hello, World! Hello again!';\n$char = 'H';\n$result = findSecondToLastOccurrence($string, $char);\nif ($result !== false) {\n    echo "The second-to-last occurrence of '$char' is at position $result.";\n} else {\n    echo "The character '$char' appears less than two times.";\n}