// Assume you have a database connection established and a caching layer set up\n\n$imagePath = 'path/to/image.jpg';\n$altText = '';\n\n// Try to fetch ALT text from database\n$dbResult = $db->query("SELECT alt_text FROM images WHERE image_path = '$imagePath'");\nif ($dbResult) {\n    $altText = $dbResult->alt_text;\n} else {\n    // Fallback to EXIF metadata if database lookup fails\n    $exifData = exif_read_data($imagePath, 'IFD0');\n    if (isset($exifData['ImageDescription'])) {\n        $altText = $exifData['ImageDescription'];\n    }\n}\n\n// Cache the result for future lookups\n$cache->set($imagePath, $altText);\n\n// Output the ALT text with the image\necho '<img src="' . $imagePath . '" alt="' . $altText . '">';