<?php\n// Configuration\n$username = 'your_username';\n$password = 'your_password'; // Store a hashed version of the password\n$directory = '/path/to/protected/directory';\n\n// Start session\nsession_start();\n\n// Check if user is already logged in\nif (isset($_SESSION['authenticated']) && $_SESSION['authenticated'] === true) {\n    header("Location: $directory/index.php");\n    exit;\n}\n\n// Handle login form submission\nif (isset($_POST['username']) && isset($_POST['password'])) {\n    $inputUsername = filter_var($_POST['username'], FILTER_SANITIZE_STRING);\n    $inputPassword = filter_var($_POST['password'], FILTER_SANITIZE_STRING);\n\n    // Verify credentials\n    if ($inputUsername === $username && password_verify($inputPassword, $password)) {\n        $_SESSION['authenticated'] = true;\n        header("Location: $directory/index.php");\n        exit;\n    } else {\n        // Display error message\n        echo 'Invalid username or password';\n    }\n}\n\n// Display login form\n?>\n<form action="login.php" method="post">\n    <label for="username">Username:</label>\n    <input type="text" id="username" name="username"><br><br>\n    <label for="password">Password:</label>\n    <input type="password" id="password" name="password"><br><br>\n    <input type="submit" value="Login">\n</form>