// Example using Redis to cache query results\n$redis = new Redis();\n$redis->connect('localhost', 6379);\n\n// Cache the results of a query for 1 hour\n$cacheKey = 'user:' . $userId . ':saved-searches';\nif (!$redis->exists($cacheKey)) {\n    $results = $db->query('SELECT * FROM saved_searches WHERE user_id = ?', $userId);\n    $redis->set($cacheKey, json_encode($results));\n    $redis->expire($cacheKey, 3600); // 1 hour\n}\n\n// Fetch the cached results\n$cachedResults = json_decode($redis->get($cacheKey), true);