function generateDigestAuthorization($wwwAuthenticateHeader, $username, $password, $method, $uri) {\n    // Parse WWW-Authenticate header\n    preg_match('/nonce="([^"]+)"/', $wwwAuthenticateHeader, $nonceMatch);\n    preg_match('/realm="([^"]+)"/', $wwwAuthenticateHeader, $realmMatch);\n    preg_match('/qop="([^"]+)"/', $wwwAuthenticateHeader, $qopMatch);\n    $nonce = $nonceMatch[1];\n    $realm = $realmMatch[1];\n    $qop = $qopMatch[1];\n\n    // Client nonce and counter\n    $cnonce = uniqid();\n    $nc = '00000001'; // Counter, should increase for each request but here it's fixed for simplicity\n\n    // Calculate response\n    $a1 = md5($username . ':' . $realm . ':' . $password);\n    $a2 = md5($method . ':' . $uri);\n    $response = md5($a1 . ':' . $nonce . ':' . $nc . ':' . $cnonce . ':' . $qop . ':' . $a2);\n\n    // Generate Authorization header\n    $authorizationHeader = 'Digest username="' . $username . '", realm="' . $realm . '", nonce="' . $nonce . '", response="' . $response . '", uri="' . $uri . '", qop=' . $qop . ', nc=' . $nc . ', cnonce="' . $cnonce . '"';\n\n    return $authorizationHeader;\n}\n\n// Example usage\n$wwwAuthHeader = 'Digest realm="Test Realm", nonce="dcd98b7102dd2f0e8b11d0f600bfb0c093", qop="auth"';\n$username = 'your_username';\n$password = 'your_password';\n$method = 'GET';\n$uri = '/path/to/resource';\n\n$authorizationHeader = generateDigestAuthorization($wwwAuthHeader, $username, $password, $method, $uri);\necho $authorizationHeader;