require_once 'mwclient.php';\n\nfunction getInfobox($country) {\n  $wiki = new mwclient('en.wikipedia.org');\n  $page = $wiki->getPage($country);\n  $html = $page->getParsedText();\n  $dom = new DOMDocument();\n  $dom->loadHTML($html);\n  $xpath = new DOMXPath($dom);\n  $infobox = $xpath->query("//table[@class='infobox']");\n  if ($infobox->length > 0) {\n    return $infobox->item(0)->ownerDocument->saveHTML($infobox->item(0));\n  } else {\n    return null;\n  }\n}\n\n$country = "France";\n$infobox = getInfobox($country);\nif ($infobox) {\n  echo $infobox;\n} else {\n  echo "No infobox found";\n}