function getInfobox($country) {\n  $url = "https://en.wikipedia.org/w/api.php?action=parse&page=$country&format=json";\n  $json = json_decode(file_get_contents($url), true);\n  $html = $json['parse']['text']['*'];\n  $dom = new DOMDocument();\n  $dom->loadHTML($html);\n  $xpath = new DOMXPath($dom);\n  $infobox = $xpath->query("//table[@class='infobox']");\n  if ($infobox->length > 0) {\n    return $infobox->item(0)->ownerDocument->saveHTML($infobox->item(0));\n  } else {\n    return null;\n  }\n}\n\n$country = "France";\n$infobox = getInfobox($country);\nif ($infobox) {\n  echo $infobox;\n} else {\n  echo "No infobox found";\n}