function get_images_from_url($url) {\n  // Initialize curl\n  $ch = curl_init($url);\n  curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\n  $html = curl_exec($ch);\n  curl_close($ch);\n\n  // Create a new DOMDocument object\n  $doc = new DOMDocument();\n  @$doc->loadHTML($html);\n\n  // Get all img tags\n  $img_tags = $doc->getElementsByTagName('img');\n\n  // Filter images by size (optional)\n  $min_width = 200;\n  $min_height = 200;\n  $images = array();\n  foreach ($img_tags as $img_tag) {\n    $width = $img_tag->getAttribute('width');\n    $height = $img_tag->getAttribute('height');\n    if ($width >= $min_width && $height >= $min_height) {\n      $images[] = $img_tag->getAttribute('src');\n    }\n  }\n\n  return $images;\n}\n\n// Example usage\n$url = 'https://example.com';\n$images = get_images_from_url($url);\nprint_r($images);