if (isset($_COOKIE['hello'])) {\n    echo "yes"; exit;\n} else {\n    // try to get the cookie from the request headers\n    if (isset($_SERVER['HTTP_COOKIE'])) {\n        $cookies = explode(';', $_SERVER['HTTP_COOKIE']);\n        foreach ($cookies as $cookie) {\n            list($name, $value) = explode('=', $cookie, 2);\n            if (trim($name) == 'hello') {\n                echo "yes"; exit;\n            }\n        }\n    }\n}