// Routing mechanism\nclass Router {\n    public function resolveUrl($url) {\n        // Parse URL and extract page name and parameters\n        $pageName = 'article';\n        $params = array('full', '18543', 'how-to-lose-weight');\n\n        // Retrieve page configuration from database\n        $pageConfig = PageConfig::findByPageName($pageName);\n\n        // Deserialize module configurations\n        $moduleConfigs = unserialize($pageConfig->module_config);\n\n        // Iterate over module configurations and pass parameters to primary module\n        foreach ($moduleConfigs as $moduleConfig) {\n            if ($moduleConfig['position'] === 'primary') {\n                // Pass parameters to primary module\n                $module = Module::findByName($moduleConfig['module_name']);\n                $module->handleRequest($params);\n                break;\n            }\n        }\n    }\n}\n\n// Module configuration array\n$moduleConfigs = array(\n    array(\n        'module_id' => 1,\n        'module_name' => 'shoutbox',\n        'position' => 'sidebar',\n        'params' => array(),\n    ),\n    array(\n        'module_id' => 2,\n        'module_name' => 'article',\n        'position' => 'primary',\n        'params' => array('full', '18543', 'how-to-lose-weight'),\n    ),\n);