amespace MyPackage;\n\nclass MyClass {\n    private $privateField;\n    protected $protectedField;\n\n    public function __construct() {\n        $this->privateField = 'private value';\n        $this->protectedField = 'protected value';\n    }\n\n    public function getPrivateField() {\n        return $this->privateField;\n    }\n\n    public function getProtectedField() {\n        return $this->protectedField;\n    }\n}\n\nclass MyOtherClass {\n    public function __construct(MyClass $myClass) {\n        // Can access protected field\n        echo $myClass->getProtectedField();\n\n        // Cannot access private field directly\n        // echo $myClass->privateField; // Error\n\n        // Can access private field through public method\n        echo $myClass->getPrivateField();\n    }\n}