$xmlUrl = 'http://freshmeat.net/projects-xml/mysql/mysql.xml?branch_id=46519';\n\n$xmlReader = new XMLReader();\n$xmlReader->open($xmlUrl);\n\nwhile ($xmlReader->read()) {\n    if ($xmlReader->nodeType == XMLReader::ELEMENT && $xmlReader->name == 'version') {\n        $version = $xmlReader->readString();\n        break;\n    }\n}\n\n$xmlReader->close();\n\necho "Latest stable version: $version";