// Get the current query string variables\n$queryVars = $_GET;\n\n// Modify the variables as needed\n$queryVars['page'] = 3; // Change the page to 3\n// or\n$queryVars['sort'] = 'title'; // Change the sort method to title\n\n// Rebuild the query string\n$newQueryString = http_build_query($queryVars);\n\n// Create the new URL\n$newUrl = 'index.php?' . $newQueryString;\n\necho '<a href="' . $newUrl . '">Link to page 3</a>';