# Simple example of a discriminative algorithm (Logistic Regression) in Python\nimport numpy as np\n\n# Sigmoid function\ndef sigmoid(x):\n    return 1 / (1 + np.exp(-x))\n\n# Logistic Regression\ndef logistic_regression(x, w):\n    return sigmoid(np.dot(x, w))\n\n# Training process\nfor epoch in range(100):\n    # Sample data\n    x = np.random.rand(1, 784)\n    y = np.random.randint(0, 2)\n    \n    # Predict probability\n    prob = logistic_regression(x, np.random.rand(784))\n    \n    # Train model\n    loss = -y * np.log(prob) - (1 - y) * np.log(1 - prob)