# API example (just a function definition)\ndef order_food(order):\n    # implementation details hidden\n    return "Food prepared"\n\n# SDK example (a package with tools and libraries)\nclass Restaurant:\n    def __init__(self):\n        self.recipes = ["burger", "fries"]\n        self.cooking_techniques = ["grill", "fry"]\n\n    def prepare_food(self, order):\n        if order in self.recipes:\n            # use cooking techniques to prepare food\n            return "Food prepared"\n        else:\n            return "Order not found"\n\n# Developer using the API\norder = "burger"\nresult = order_food(order)\nprint(result)  # Output: "Food prepared"\n\n# Developer using the SDK\nrestaurant = Restaurant()\norder = "burger"\nresult = restaurant.prepare_food(order)\nprint(result)  # Output: "Food prepared"