# Sample data\ndata = [\n    {"last_name": "Smith", "first_name": "John", "room_code": "A101"},\n    {"last_name": "Johnson", "first_name": "Jane", "room_code": "B202"},\n    # ... more data ...\n]\n\n# Define column widths\ncolumn_widths = [20, 20, 10]  # last_name, first_name, room_code\n\n# Print header\nprint("{:<{width}} {:<{width}} {:<{width}}".format(\n    "Last Name", "First Name", "Room Code", width=column_widths[0]))\n\n# Print data\nfor row in data:\n    print("{:<{width}} {:<{width}} {:<{width}}".format(\n        row["last_name"], row["first_name"], row["room_code"], width=column_widths[0]))