# views.py\nfrom django.shortcuts import render, redirect\nfrom .forms import CustomerFormSet\n\ndef update_customers(request):\n    if request.method == 'POST':\n        formset = CustomerFormSet(request.POST)\n        if formset.is_valid():\n            formset.save()\n            return redirect('success_url')\n    else:\n        formset = CustomerFormSet(queryset=Customer.objects.all())\n    return render(request, 'template.html', {'formset': formset})