# Array access\narray = [1, 2, 3]\nprint(array[0])  # prints 1\n\n# Function call\ndef func(x):\n    return x + 1\nprint(func(0))  # prints 1\n\n# Trying to call an array like a function raises an error\ntry:\n    print(array(0))\nexcept TypeError:\n    print("Error: 'list' object is not callable")\n\n# Trying to access a function like an array also raises an error\ntry:\n    print(func[0])\nexcept TypeError:\n    print("Error: 'function' object is not subscriptable")