# Example using industry benchmarks\nimport math\n\n# Define the organization size (10,000 users)\norg_size = 10000\n\n# Estimate the number of users, groups, and OUs based on industry benchmarks\nnum_users = org_size\nnum_groups = math.ceil(org_size / 10)\nnum_ous = math.ceil(num_groups / 5)\n\n# Create the directory structure\ndirectory_structure = {\n    'users': num_users,\n    'groups': num_groups,\n    'ous': num_ous\n}