# Cloud Backup Cost Estimator\n\ndata_size_gb = 2000  # 2TB in GB\naws_s3_cost_per_gb_month = 0.023\ngcp_storage_cost_per_gb_month = 0.026\nazure_blob_cost_per_gb_month = 0.024\nbackblaze_b2_cost_per_gb_month = 0.005\n\naws_s3_cost = data_size_gb * aws_s3_cost_per_gb_month\ngcp_storage_cost = data_size_gb * gcp_storage_cost_per_gb_month\nazure_blob_cost = data_size_gb * azure_blob_cost_per_gb_month\nbackblaze_b2_cost = data_size_gb * backblaze_b2_cost_per_gb_month\n\nprint("Estimated monthly costs:")\nprint(f"AWS S3: ${aws_s3_cost:.2f}")\nprint(f"Google Cloud Storage: ${gcp_storage_cost:.2f}")\nprint(f"Microsoft Azure Blob Storage: ${azure_blob_cost:.2f}")\nprint(f"Backblaze B2: ${backblaze_b2_cost:.2f}")