# views.py\nfrom django.shortcuts import get_object_or_404\nfrom django.views import View\n\nclass ObjectViewDispatcher(View):\n    def dispatch(self, request, pk):\n        obj = get_object_or_404(BaseClass, pk=pk)\n        obj.refresh_from_db()  # or use get_subclass_instance\n        view_name = f"{obj.__class__.__name__}View"\n        return getattr(self, view_name, self.default_view)(request, obj)\n\n    def default_view(self, request, obj):\n        # Handle the case where no specific view is found\n        return HttpResponse("No view found for this object type")