# Define a dictionary of user roles and their corresponding access rights\naccess_rights = {\n    'admin': ['create', 'read', 'update', 'delete'],\n    'moderator': ['read', 'update'],\n    'user': ['read']\n}\n\n# Define a function to check if a user has access to a specific resource\ndef has_access(user_role, resource):\n    return resource in access_rights.get(user_role, [])\n\n# Example usage:\nuser_role = 'moderator'\nresource = 'update'\n\nif has_access(user_role, resource):\n    print("Access granted")\nelse:\n    print("Access denied")