# settings.py\nINSTALLED_APPS = [\n    # ...\n    'django-googlemaps',\n    # ...\n]\n\n# views.py\nfrom django.shortcuts import render\nfrom django-googlemaps import GoogleMap\n\ndef map_view(request):\n    map = GoogleMap(\n        center=(37.7749, -122.4194),\n        zoom=13,\n        markers=[\n            {'title': 'Marker 1', 'position': (37.775, -122.42)},\n            {'title': 'Marker 2', 'position': (37.785, -122.43)},\n        ],\n    )\n    return render(request, 'map.html', {'map': map})\n\n# map.html\n<!DOCTYPE html>\n<html>\n<head>\n    <title>Map</title>\n    <script src="https://maps.googleapis.com/maps/api/js?key=YOUR_API_KEY"></script>\n</head>\n<body>\n    <div id="map" style="width: 500px; height: 500px;"></div>\n    {{ map.render }}\n</body>\n</html>