# views.py\nfrom django.db import transaction\n\ndef reorder_todo_items(project, new_order):\n    with transaction.atomic():\n        # Clear existing order\n        TodoItemOrder.objects.filter(project=project).delete()\n        # Create new order\n        for i, todo_item in enumerate(new_order):\n            TodoItemOrder.objects.create(project=project, todo_item=todo_item, order=i)