#!/usr/bin/env python\n\nimport sys\nimport subprocess\n\n# Set the service and Linux box details\nSERVICE = 'your_service_name'\nBOX = 'your_linux_box_ip'\n\n# Restart the service using SSH\ndef restart_service():\n    ssh_cmd = f'ssh root@{BOX} "systemctl restart {SERVICE}"'\n    subprocess.run(ssh_cmd, shell=True)\n\n# Read input from Asterisk (not used in this example)\ninput_str = sys.stdin.read()\n\n# Run the restart script\nrestart_service()\n\n# Return a success response to Asterisk\nprint("200 result=SUCCESS")