# Define the languages and their corresponding ASCII characters\nlanguages = {\n    'English': 'abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ',\n    'Hawaiian': 'abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZʻāēīōū',\n    'Indonesian': 'abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ',\n    'Swahili': 'abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ'\n}\n\n# Print out the ASCII characters used in each language\nfor language, characters in languages.items():\n    print(f"{language}: {characters}")