# Using Python's socket library\nimport socket\n\ndef test_tcp_server():\n    # Create test client\n    client_socket = socket.socket(socket.AF_INET, socket.SOCK_STREAM)\n    client_socket.connect(("localhost", 8080))  # assuming your server listens on port 8080\n\n    # Send request\n    client_socket.sendall(b"request_data")\n\n    # Receive response\n    response = client_socket.recv(1024)\n    assert response == b"expected_response"\n\n    # Close client socket\n    client_socket.close()