# Python web application example\nfrom flask import Flask, jsonify\nimport requests\n\napp = Flask(__name__)\n\n@app.route('/api/main-endpoint', methods=['GET'])\ndef main_endpoint():\n    # Call C++ microservice\n    url = 'http://localhost:8080/api/performance-critical-task'\n    response = requests.get(url)\n\n    # Process response from C++ microservice\n    result = response.json()\n\n    return jsonify({'result': result})\n\nif __name__ == '__main__':\n    app.run(debug=True)