# testing_policy.py\nimport unittest\n\nclass TestingPolicy:\n    def __init__(self, codebase):\n        self.codebase = codebase\n\n    def pre_checkin(self):\n        # Run tests before checking in code\n        test_suite = unittest.TestLoader().loadTestsFromModule(self.codebase)\n        unittest.TextTestRunner().run(test_suite)\n\n    def post_task_completion(self):\n        # Run tests after marking a task as complete\n        test_suite = unittest.TestLoader().loadTestsFromModule(self.codebase)\n        unittest.TextTestRunner().run(test_suite)