# in the child script\nwith open('flag.txt', 'w') as f:\n    f.write('1')\n\n# in the main script\nimport time\n\nwhile True:\n    try:\n        with open('flag.txt', 'r') as f:\n            flag = f.read()\n            if flag == '1':\n                # do something\n                print("Flag is set")\n                break\n    except FileNotFoundError:\n        pass\n    time.sleep(1)