# views.py\nfrom django.http import HttpResponse\nfrom django.template import loader\n\ndef index(request):\n    # Get the session expiration time\n    session_expiration = request.session.get_expiry_date()\n\n    # Calculate the remaining session time\n    remaining_time = session_expiration - datetime.now()\n\n    # Render the template with the remaining session time\n    template = loader.get_template('index.html')\n    context = {'remaining_time': remaining_time}\n    return HttpResponse(template.render(context, request))\n\n# index.html\n<script>\n  // Countdown function\n  function countdown(endTime) {\n    var remainingTime = endTime - new Date().getTime();\n    if (remainingTime <= 0) {\n      alert('Session expired!');\n    } else {\n      setTimeout(function() {\n        countdown(endTime);\n      }, 1000);\n    }\n  }\n\n  // Start the countdown\n  countdown({{ remaining_time|date:'U' }});\n</script>