# Test for User model\nclass TestUser(unittest.TestCase):\n    def test_create_user(self):\n        user = User(name='John Doe', email='johndoe@example.com')\n        self.assertEqual(user.name, 'John Doe')\n        self.assertEqual(user.email, 'johndoe@example.com')\n\n# Test for SearchController\nclass TestSearchController(unittest.TestCase):\n    def test_search_users_by_name(self):\n        # Mock the user repository\n        users = [User(name='John Doe', email='johndoe@example.com')]\n        repository = mock.Mock(return_value=users)\n\n        # Test the search functionality\n        controller = SearchController(repository)\n        results = controller.search_users_by_name('John')\n        self.assertEqual(len(results), 1)\n        self.assertEqual(results[0].name, 'John Doe')