# test_harness.py\nimport subprocess\nimport time\n\ndef test_xml_parser(xml_string):\n    # Call the Java library\n    start_time = time.time()\n    subprocess.run(["java", "-jar", "java_xml_parser.jar", xml_string])\n    end_time = time.time()\n    print("Java library took:", end_time - start_time)\n\n    # Call the C# library\n    start_time = time.time()\n    subprocess.run(["mono", "csharp_xml_parser.exe", xml_string])\n    end_time = time.time()\n    print("C# library took:", end_time - start_time)