# models.py\nfrom django.db import models\n\nclass Person(models.Model):\n    name = models.CharField(max_length=100)\n\nclass Home(models.Model):\n    person = models.ForeignKey(Person, on_delete=models.CASCADE)\n    address = models.CharField(max_length=100)\n\nclass Room(models.Model):\n    home = models.ForeignKey(Home, on_delete=models.CASCADE)\n    name = models.CharField(max_length=100)\n\n# views.py or wherever you're querying the data\nfrom django.db.models import Prefetch\n\npersons = Person.objects.prefetch_related(\n    Prefetch('home_set', queryset=Home.objects.prefetch_related('room_set'))\n).filter(home__room__isnull=False)\n\nfor person in persons:\n    for home in person.home_set.all():\n        for room in home.room_set.all():\n            # do something with room\n            print(room.name)