# models.py (shared between API and web interface)\nclass User:\n    def __init__(self, id, name, email):\n        self.id = id\n        self.name = name\n        self.email = email\n\n    def save(self):\n        # DB logic to save the user\n        pass\n\n    def delete(self):\n        # DB logic to delete the user\n        pass\n\n# api.py (API-specific code)\nfrom models import User\n\nclass UserService:\n    def create_user(self, data):\n        user = User(**data)\n        user.save()\n        return user\n\n    def update_user(self, user_id, data):\n        user = User.get(user_id)\n        user.update(**data)\n        user.save()\n        return user\n\n    def delete_user(self, user_id):\n        user = User.get(user_id)\n        user.delete()\n        return {"message": "User deleted"}\n\n# web_interface.py (web interface-specific code)\nfrom models import User\n\nclass UserController:\n    def create_user(self, request):\n        data = request.POST\n        user = User(**data)\n        user.save()\n        return redirect("user_list")\n\n    def update_user(self, request, user_id):\n        user = User.get(user_id)\n        user.update(**request.POST)\n        user.save()\n        return redirect("user_list")\n\n    def delete_user(self, request, user_id):\n        user = User.get(user_id)\n        user.delete()\n        return redirect("user_list")