# Define the arrays\nall_items = ["Table", "Chair", "TV", "Fireplace", "Bed"]\nhouse_items = ["Table", "TV", "Bed"]\n\n# Convert the arrays to sets for efficient lookups\nall_items_set = set(all_items)\nhouse_items_set = set(house_items)\n\n# Calculate the items in the house (HAS)\nhas_items = [item for item in all_items if item in house_items_set]\n\n# Calculate the items not in the house (NEEDS)\nneeds_items = [item for item in all_items if item not in house_items_set]\n\n# Print the results\nprint("HAS:", has_items)\nprint("NEEDS:", needs_items)