# Define a simple averaging function with integer division and rounding\ndef avg_speed(distance, time):\n    return round(distance / time, 1)\n\n# Initial values\ndistance = 1234  # meters\ntime = 591  # seconds\n\n# Calculate initial average speed\navg = avg_speed(distance, time)\nprint("Initial average speed:", avg)\n\n# Increase time by 1 second\ntime += 1\navg = avg_speed(distance, time)\nprint("Updated average speed:", avg)