# views.py\nfrom django.shortcuts import render\nfrom .forms import TimeWorkedForm\n\ndef create_time_worked(request):\n    if request.method == 'POST':\n        form = TimeWorkedForm(request.user, request.POST)\n        if form.is_valid():\n            form.save()\n            return redirect('success_url')\n    else:\n        form = TimeWorkedForm(request.user)\n    return render(request, 'template.html', {'form': form})