# views.py\nfrom django.shortcuts import render\nfrom .models import Archive, Video, Book, Audio\n\ndef create_archive(request):\n    if request.method == 'POST':\n        archive_type = request.POST['archive_type']\n        if archive_type == 'video':\n            video = Video.objects.create(title=request.POST['title'], video_url=request.POST['video_url'])\n            return render(request, 'video_form.html', {'video': video})\n        elif archive_type == 'book':\n            book = Book.objects.create(title=request.POST['title'], author=request.POST['author'])\n            return render(request, 'book_form.html', {'book': book})\n        # ...