# Example Python code using Flask and a simple session management system\nfrom flask import Flask, session, request\n\napp = Flask(__name__)\n\n@app.before_request\ndef before_request():\n    if 'session_id' not in session:\n        session['session_id'] = generate_session_id()\n    session['last_request_timestamp'] = datetime.now()\n\n@app.after_request\ndef after_request(response):\n    if 'last_request_timestamp' in session:\n        time_spent = (datetime.now() - session['last_request_timestamp']).total_seconds()\n        # Log the time spent on the page\n    return response