# Define the architecture components\ncomponents = {\n    'Legacy System': {'trust_level': 'low'},\n    'Facade': {'trust_level': 'medium'},\n    'Main Website Application': {'trust_level': 'high'}\n}\n\n# Define the interactions between components\ninteractions = [\n    {'source': 'Legacy System', 'destination': 'Facade', 'data': ' sensitive data'},\n    {'source': 'Facade', 'destination': 'Main Website Application', 'data': 'parsed data'}\n]\n\n# Identify potential threats\nthreats = []\nfor interaction in interactions:\n    if interaction['source']['trust_level'] < interaction['destination']['trust_level']:\n        threats.append({'threat': 'Data Tampering', 'description': 'Data can be tampered with in transit'})\n        threats.append({'threat': 'Data Disclosure', 'description': 'Data can be disclosed to unauthorized parties'})\n\n# Print the identified threats\nfor threat in threats:\n    print(threat['threat'], threat['description'])