# application.py\nimport gtk\nfrom toolbar_controller import ToolbarController\nfrom toolbar_view import ToolbarView\n\nclass Application:\n    def __init__(self):\n        self.window = gtk.Window()\n        self.toolbar_view = ToolbarView()\n        self.toolbar_controller = ToolbarController(self.toolbar_view)\n        self.window.add(self.toolbar_view)\n        self.window.show_all()