# Example Locust task\nfrom locust import HttpLocust, TaskSet, task\n\nclass VideoStreamingTaskSet(TaskSet):\n    @task\n    def video_request(self):\n        self.client.get("/your-video-content")\n\nclass VideoStreamingLocust(HttpLocust):\n    task_set = VideoStreamingTaskSet\n    min_wait = 0\n    max_wait = 0\n\nlocust -f path/to/your/locustfile.py --headless -u 5000 -r 1000 --run-time 1h