# models/vertex.rb\nclass Vertex < ApplicationRecord\n  has_many :edges, dependent: :destroy\nend\n\n# models/edge.rb\nclass Edge < ApplicationRecord\n  belongs_to :vertex1, class_name: 'Vertex', foreign_key: 'vertex1_id'\n  belongs_to :vertex2, class_name: 'Vertex', foreign_key: 'vertex2_id'\n  \n  # Validate that vertex1_id is less than vertex2_id to prevent duplicates\n  validates :vertex1_id, presence: true\n  validates :vertex2_id, presence: true, comparison: { greater_than: :vertex1_id }\nend